script:
frappe.ui.form.on('Vehicle', {
    refresh(frm) {
        if (frm.doc.custom_imei_number) {  // Ensure the IMEI field is not empty
            frappe.call({
                method: 'gps_integration.services.rest.get_imei_status',
                args: {
                    'imei_to_search': frm.doc.custom_imei_number  // Pass the IMEI as an argument
                },
                callback: function(r) {
                    if (!r.exc) {
                        if (r.message) {
                            if (r.message === "Online") {
                                frm.set_intro(r.message, 'blue');
                            } else if (r.message) {
                                frm.set_intro(r.message, 'red');
                            } else {
                                frappe.msgprint(`IMEI: ${frm.doc.custom_imei_number} not found.`);
                            }

                        } else {
                            frappe.msgprint(`IMEI: ${frm.doc.custom_imei_number} not found.`);
                        }
                    }
                }
            });
        } else {
            frappe.msgprint('Please enter a valid IMEI number.');
        }
    }
});
